init:
struct TreeNode {
    id: ''
    parent: null
    x: 0
    y: 0
    siblingIdx: 0
    level: 0
    fullWidth: 0
    children: List()

    attachTo(parentNode) {
        this.parent = parentNode
        this.siblingIdx = parentNode.children.size
        parentNode.children.add(this)
    }

    attachChildAtIndex(childNode, idx) {
        childNode.parent = this
        this.children.insert(idx, childNode)
        this.children.forEach((node, nodeIdx) => {
            node.siblingIdx = nodeIdx
        })
    }

    encodeTree() {
        encoded = this.id
        this.children.forEach((childNode) => {
            childEncoded = childNode.encodeTree()
            encoded += ',' + childEncoded
        })
        encoded + ',N'
    }

    reindex() {
        this.level = if (this.parent) { this.parent.level + 1 } else { 0 }
        if (this.children.size > 0) {
            fullWidth = 0
            this.children.forEach((childNode) => {
                childNode.reindex()
                fullWidth += childNode.fullWidth
            })
            this.fullWidth = fullWidth + (this.children.size - 1) * hGap
        } else {
            this.fullWidth = width
        }
    }
}

decodeTree = (encodedText) => {
    nodeList = splitString(encodedText, ',')
    rootNode = TreeNode(nodeList.shift())
    currentNode = rootNode
    while(nodeList.size > 0) {
        nodeId = nodeList.shift()
        if (nodeId == 'N') {
            parent = currentNode.parent
            if (!parent) {
                parent = rootNode
            }
            currentNode = parent
        } else {
            newNode = TreeNode(nodeId)
            newNode.attachTo(currentNode)
            currentNode = newNode
        }
    }
    rootNode.reindex()
    rootNode
}

struct Connector {
    id: ''
    srcId: ''
    dstId: ''
    p1: Vector(0, 0)
    p2: Vector(0, 0)
    x: 0
    y: 0
    w: 0
    h: 0

    updateArea() {
        this.x = min(this.p1.x, this.p2.x)
        this.y = min(this.p1.y, this.p2.y)
        this.w = abs(this.p1.x - this.p2.x)
        this.h = abs(this.p1.y - this.p2.y)
    }
}

items = List()
rootNode = decodeTree(nodes)

connectors = List()

nodesById = Map()

visitTree = (node, parent) => {
    nodesById.set(node.id, node)

    if (!parent) {
        node.x = 0
        node.y = 0
    } else {
        node.y = node.level * (height + vGap)
    }
    items.add(node)

    axis = node.x + width/2
    childOffset = axis - node.fullWidth/2
    node.children.forEach((childNode) => {
        childFullWidth = childNode.fullWidth
        childNode.x = childOffset + (childFullWidth - width)/2
        childOffset = childNode.x + (width + childFullWidth)/2 + hGap
        visitTree(childNode, node)

        connector = Connector(`connector_${node.id}_${childNode.id}`, node.id, childNode.id)
        connector.p1 = Vector(node.x + width/2, node.y + height)
        connector.p2 = Vector(childNode.x + width/2, childNode.y)
        connector.updateArea()
        connectors.add(connector)
    })
}

visitTree(rootNode, 0, null)

createNewChildFor = (nodeId) => {
    node = nodesById.get(nodeId)
    if (node) {
        TreeNode(uid()).attachTo(node)
        rootNode.reindex()
    }
    nodes = rootNode.encodeTree()
}

createNewSiblingFor = (nodeId, idx) => {
    node = nodesById.get(nodeId)
    if (node && node.parent) {
        node.parent.attachChildAtIndex(TreeNode(uid()), idx)
        rootNode.reindex()
    }
    nodes = rootNode.encodeTree()
}

on('delete', (itemId, item) => {
    node = nodesById.get(itemId)
    if (node && node.parent) {
        node.parent.children.remove(node.siblingIdx)
        rootNode.reindex()
    }
    nodes = rootNode.encodeTree()
})
